collapsed\":\"0\",\"level\":\"0\",\"fields\":{\"images\":{\"0\":\"98\",\"focus-attr\":[{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"}]}}},\"64\":{\"modified\":\"1\",\"type\":\"paragraph\",\"enabled\":\"1\",\"collapsed\":\"0\",\"level\":\"0\",\"fields\":{\"highlight\":\"\",\"cssClasses\":\"\",\"paragraph\":\"<h2>Mockups<\\/h2>\\r\\n<p>High fidelity mockups.<br>Prototypes<\\/p>\"}},\"65\":{\"modified\":\"1\",\"type\":\"gallery\",\"enabled\":\"1\",\"collapsed\":\"0\",\"level\":\"0\",\"fields\":{\"images\":{\"0\":\"92\",\"1\":\"91\",\"2\":\"93\",\"3\":\"90\",\"4\":\"89\",\"focus-attr\":[{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"},{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"},{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"},{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"},{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"}]}}}}}}', '2017-10-26 12:48:41', '2017-10-26 12:48:41', 'c0ba89a9-a3cc-4970-b6b9-fba9314e324f'),
