\/ul>\\r\\n<p><br><\\/p>\"}},\"99\":{\"modified\":\"1\",\"type\":\"gallery\",\"enabled\":\"1\",\"collapsed\":\"0\",\"level\":\"0\",\"fields\":{\"images\":{\"0\":\"98\",\"focus-attr\":[{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"}]}}},\"65\":{\"modified\":\"1\",\"type\":\"gallery\",\"enabled\":\"1\",\"collapsed\":\"0\",\"level\":\"0\",\"fields\":{\"images\":{\"0\":\"92\",\"1\":\"91\",\"2\":\"93\",\"3\":\"90\",\"4\":\"89\",\"focus-attr\":[{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"},{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"},{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"},{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"},{\"data-focus-x\":\"0.00\",\"data-focus-y\":\"0.00\"}]}}}}}}', '2017-10-26 12:56:03', '2017-10-26 12:56:03', 'd50f7ea9-fe37-471f-b17a-026c32eaa57e'),
